  function preposicoes() {

    $palavras = explode(" ", $this->textoB);
    $cincoLetras = [];
    $foo = [];
    $bar = [];
    $comL = [];
    $preposicoes = [];

    //separa o array do texto
    foreach ($palavras as $palavra) {
      //se a palavra tiver 5 letras
      if (strlen($palavra) == 5) {
        $cincoLetras[] = $palavra;
      }

    }

    //separa o array de palavras com 5 letras
    foreach ($cincoLetras as $cinco) {
      //veirifca se tem final das palavras foo...
      if (substr($cinco, -1) == 'r' || substr($cinco, -1) == 't' || substr($cinco, -1) == 'c' || substr($cinco, -1) == 'd' || substr($cinco, -1) == 'b') {
          $foo[] = $cinco;
      }else {
        //as palavras que não tiverem...
        $bar[] = $cinco;
      }
    }

    //separa as palavras do tipo bar...
    foreach ($bar as $semL) {
      //verifica se tem L na palavra...
      if (strpos($semL, "l") !== false) {
        $comL[] = $semL;
      }else {
        //as que não tiverem...
        $preposicoes[] = $semL;
      }
    }
    //exibe a resposta
    return 'Existem '. count($preposicoes) .' preposições no Texto B';
  }

  function verbos() {

    $palavras = explode(" ", $this->textoB);
    $verbos = [];
    $foo = [];
    // $primeiraPessoa = [];
    $finalVerbos = [];

    //separa o array do texto
    foreach ($palavras as $palavra) {
      //se a palavra tiver 8 letras ou mais
      if (strlen($palavra) >= 8) {
        $verbos[] = $palavra;
      }
    }

    //separa o array de palavras com 8 letras ou mais
    foreach ($verbos as $verbo) {
      //veirifca se tem final das palavras foo...
      if (substr($verbo, -1) == 'r' || substr($verbo, -1) == 't' || substr($verbo, -1) == 'c' || substr($verbo, -1) == 'd' || substr($verbo, -1) == 'b') {
          $foo[] = $verbo;
      }else {
        //as palavras que não tiverem...
        $finalVerbos[] = $verbo;
      }
    }

  $primeirasLetras = [];

  //separa os verbos
  foreach($finalVerbos as $primeiraLetra) {
    //pega a primeira letra das palabras
    if (empty($primeirasLetras[$primeiraLetra[0]])){
      $primeirasLetras[$primeiraLetra[0]][]= $primeiraLetra[0];
    }
    $primeirasLetras[$primeiraLetra[0]][] = $primeiraLetra;
  }

  $verbosNormais = [];
  $verbosPrimeiraPessoa = [];
  //separa as primeiras letras
  foreach ($primeirasLetras as $a) {
    //veririca se começam com letras tipo foo
    if ($a == 'r' || $a == 't' || $a == 'c' || $a == 'd' || $a == 'b') {
        $verbosNormais[] = $a;
    }else {
      //as palavras que não tiverem...
      $verbosPrimeiraPessoa[] = $a;
    }
  }

    //retorna a resposta
    return 'Existem '. count($finalVerbos) .' verbos no Texto B </br> Há '. count($verbosPrimeiraPessoa) .' verbos em primeira pessoa no Texto B';
  }


